mapper.insert:
INSERT INTO bucket_study
(
	customer_id,
	study_id
)
VALUES
(
	#{customerId},
	#{studyId}
)

mapper.update:
UPDATE bucket_study SET
	customer_id=#{customerId},
	study_id = #{studyId}
WHERE
	customer_id = #{customerId}
	AND
	study_id = #{studyId}

mapper.delete:
DELETE FROM
	bucket_study
WHERE
	customer_id = #{customerId}
AND
	study_id = #{studyId}
