mapper.select:
SELECT

FROM
study
WHERE
study_id = #{studyId}
order by study_regdate DESC;

mapper.update:
UPDATE study SET study_id = #{studyId}, teacher_id = #{teacherId}, teacher_name = #{teacherName}, study_name = #{studyName}, study_info = #{studyInfo}, study_job = #{studyJob}, study_price = #{studyPrice}, study_progressName = #{studyProgressName}, study_programLanguage = #{studyProgramLanguage}, study_week = #{studyWeek},
study_startTime = #{studyStartTime}, study_endTime = #{studyEndTime}, study_startDate = #{studyStartDate}, study_endDate = #{studyEndDate},
study_location = #{studyLocation}, study_amount = #{studyAmount}, study_reference = #{studyReference}, study_check = #{studyCheck}
